mapper.update:
update time_line_item
set create_time = #{createTime,jdbcType=TIMESTAMP},
  msg = #{msg,jdbcType=VARCHAR},
  `type` = #{type,jdbcType=VARCHAR},
  update_time = #{updateTime,jdbcType=TIMESTAMP}
where time_line_item_id = #{timeLineItemId,jdbcType=INTEGER}

mapper.select:
select time_line_item_id, create_time, msg, `type`, update_time
from time_line_item
where time_line_item_id = #{timeLineItemId,jdbcType=INTEGER}
