mapper.update:
update project_status
set create_time = #{createTime,jdbcType=TIMESTAMP},
  update_time = #{updateTime,jdbcType=TIMESTAMP},
  base_info_id = #{baseInfoId,jdbcType=VARCHAR},
  time_line_item_id = #{timeLineItemId,jdbcType=INTEGER}
where project_status_id = #{projectStatusId,jdbcType=VARCHAR}

mapper.select:
select project_status_id, create_time, update_time, base_info_id, time_line_item_id
from project_status
where project_status_id = #{projectStatusId,jdbcType=VARCHAR}
